mapper.insert:
INSERT INTO tb_board (

) VALUES (
/* #{idx}*/
default /*postgresql serial == mysql auto increment*/
, #{title}
, #{content}
, #{writer}
, 0
, coalesce(cast(#{noticeYn} as enum), 'N') /*mysql의 enum(여러값중 허용된값만 저장가능) */
, coalesce(cast(#{secretYn} as enum), 'N') /*mysql ifnull == postgresql coalesce*/
, 'N'
/*, now()*  mysql */
, current_timestamp /* postgresql */
, NULL
, NULL
)

mapper.update:
UPDATE tb_board
SET update_time = NOW()
  , title       = #{title}
  , content     = #{content}
  , writer      = #{writer}
  , notice_yn   = coalesce(cast(#{noticeYn} as enum), 'N')
  , secret_yn   = coalesce(casT(#{secretYn} as enum), 'N')
WHERE idx = #{idx}
